$Title = "DRS Rules"
$Header = "DRS Rules"
$Display = "Table"
$Author = "John Sneddon"
$PluginVersion = 1.2
$PluginCategory = "vSphere"

# Start of Settings 
# Display VM affinity rules?
$ShowVMAffinity = $true
# Display VM anti-affinity rules?
$ShowVMAntiAffinity = $true
# Display HOSTaffinity rules?
$ShowHostAffinity = $true
# Set DRS Rule name exception (regex)
$excludeName = "ExcludeMe"
# End of Settings

# Update settings where there is an override
$ShowVMAffinity = Get-vCheckSetting $Title "ShowVMAffinity" $ShowVMAffinity
$ShowVMAntiAffinity = Get-vCheckSetting $Title "ShowVMAntiAffinity" $ShowVMAntiAffinity
$ShowHostAffinity = Get-vCheckSetting $Title "ShowHostAffinity" $ShowHostAffinity
$excludeName = Get-vCheckSetting $Title "excludeName" $excludeName

#Compile an array of rule types to return
$Types = @()
if ($ShowVMAffinity)     { $Types += "VMAffinity"}
if ($ShowVMAntiAffinity) { $Types += "VMAntiAffinity"}
if ($ShowHostAffinity)   { $Types += "VMHostAffinity"}

$Clusters | Foreach-Object {
   Get-DrsRule -Cluster $_ -Type $Types | Where-Object { $_.Name -notmatch $excludeName } | 
   Select-Object Cluster, Enabled, Name, Type, @{N="VM";E={(Get-View $_.VMIDS | Select-Object -ExpandProperty Name) -join "<br />"}},
     @{N="Rule Host";E={(Get-View $_.AffineHostIds | Select-Object -ExpandProperty Name) -join "<br />" }},
     @{N="Running on";E={(Get-View (Get-View $_.VMIDS | Foreach-Object {$_.Runtime.Host}) | Select-Object ExpandProperty Name) -join "<br />"}}
}

$Comments = ("Contains all DRS rules defined in this vCenter - {0}" -f ($types -join ","))

# Add pretty icons
Add-ReportResource -cid "Error" -Type "SystemIcons" -ResourceData "Error"
Add-ReportResource -cid "OK" -Type "base64" -ResourceData "png|"

# Table formatting rules 
$TableFormat = @{"Enabled" = @(@{ "-eq `$true"     = "Cell,cid|OK|16x16"; },
                               @{ "-eq `$false"    = "Cell,cid|Error|16x16" })
                 }
                 
